mapper.select:
SELECT COUNT(entityGuid)
FROM ${prefix}entity entity
WHERE entity.codingSchemeGuid = #{param1}

SELECT
entityGuid AS uidKey
FROM
${prefix}entity entity
WHERE
codingSchemeGuid = #{param1}
AND
entity.entityCode = #{param2}

    AND
    entity.entityCodeNamespace = #{param3}



   WHERE
   entity.codingSchemeGuid = #{param1}
   AND
   entity.entityGuid IN (  
   #{param2[]}
)


WHERE
entity.entityGuid = #{param1}
AND
entity.codingSchemeGuid = #{param2}


WHERE
entity.entityGuid = #{param1}
AND
entity.entryStateGuid = #{param2}


WHERE
codingSchemeGuid = #{param1}
AND
entityCode = #{param2}
AND
entity.entityCodeNamespace = #{param3}


WHERE
codingSchemeGuid = #{param1}
AND
entityCode = #{param2}


WHERE
codingSchemeGuid = #{param1}
AND
entityCode = #{param2}
AND
entity.entityCodeNamespace = #{param3}
AND
entityType.entityType = 'association'



WHERE
entity.entityGuid = #{param1}


    AND
    currentRevision.revisionId = #{revisionId}


    AND
    entryState.revisionGuid IS NULL

SELECT *
FROM ${prefix}entity
WHERE entityGuid = #{param1}

SELECT entityGuid AS uidKey
FROM ${prefix}entity entity
WHERE entity.codingSchemeGuid = #{param1}

SELECT entryStateGuid AS uidKey
FROM ${prefix}entity en
WHERE en.entityGuid = #{param1}

SELECT prevEntryStateGuid AS uidKey
FROM ${prefix}entryState es
WHERE es.entryStateGuid = #{param1}

SELECT propertyId
FROM ${prefix}property
WHERE referenceGuid = #{param1}
  AND referenceType = #{param2}

SELECT entityType
FROM ${prefix}entityType
WHERE entityGuid = #{param1}

SELECT description
FROM ${prefix}entity entity
WHERE entity.codingSchemeGuid = #{param1}
  AND entity.entityCode = #{param2}
  AND entity.entityCodeNamespace = #{param3}

SELECT description
FROM ${prefix}entity entity
WHERE entity.entityCode = #{param1}
  AND entity.entityCodeNamespace = #{param1}

SELECT DISTINCT entity.entityCodeNamespace FROM ${prefix}entity entity WHERE entity.codingSchemeGuid = #{param1} AND entity.entityCode = #{param2}
UNION
SELECT DISTINCT eate.sourceEntityCodeNamespace FROM ${prefix}entityAssnsToEntity eate INNER JOIN ${prefix}associationPredicate ap ON eate.associationPredicateGuid = ap.associationPredicateGuid INNER JOIN ${prefix}relation rel ON ap.relationGuid = rel.relationGuid
WHERE rel.codingSchemeGuid = #{param1} AND eate.sourceEntityCode = #{param2}
UNION
SELECT DISTINCT eate.targetEntityCodeNamespace FROM ${prefix}entityAssnsToEntity eate INNER JOIN ${prefix}associationPredicate ap ON eate.associationPredicateGuid = ap.associationPredicateGuid INNER JOIN ${prefix}relation rel ON ap.relationGuid = rel.relationGuid
WHERE rel.codingSchemeGuid = #{param1} AND eate.targetEntityCode = #{param2}
