
package edu.mayo.informatics.lexgrid.convert.directConversions.medDRA.Data;import java.util.ArrayList;import java.util.List;public enum MedDRA_Metadata {    HISTORY                 (MedDRA_MetadataType.INFO, "meddra_history_english.asc", "MeddraHistory", MedDRARecord_meddra_history.class),    RELEASE                 (MedDRA_MetadataType.INFO, "meddra_release.asc", "MeddraRelease", MedDRARecord_meddra_release.class),    MD_HIERARCHY            (MedDRA_MetadataType.INFO, "mdhier.asc", "MDHierarchy", MedDRARecord_mdhier.class),    SOC_INTL_MAP            (MedDRA_MetadataType.INFO, "intl_ord.asc", "MapSOC2INTL", MedDRARecord_intl_ord.class),    SMQ_LIST                (MedDRA_MetadataType.ENTITYMAP, "smq_list.asc", "SMQList", MedDRARecord_smq_list.class),    SYSTEM_ORGAN_CLASSES    (MedDRA_MetadataType.ENTITYMAP, "soc.asc", "SystemOrganClasses",  MedDRARecord_soc.class),    HIGH_LEVEL_GROUP_TERMS  (MedDRA_MetadataType.ENTITY, "hlgt.asc", "HighLevelGroups", MedDRARecord_hlgt.class),    HIGH_LEVEL_TERMS        (MedDRA_MetadataType.ENTITY, "hlt.asc", "HighLevelTerms", MedDRARecord_hlt.class),    PREF_TERMS              (MedDRA_MetadataType.ENTITY, "pt.asc", "PreferredTerms", MedDRARecord_pt.class),    SOC_HLGT_MAP            (MedDRA_MetadataType.MAP, "soc_hlgt.asc", "MapSOC2HLGT", MedDRARecord_soc_hlgt.class),    HLGT_HLT_MAP            (MedDRA_MetadataType.MAP, "hlgt_hlt.asc", "MapHLGT2HLT", MedDRARecord_hlgt_hlt.class),    HLT_PT_MAP              (MedDRA_MetadataType.MAP, "hlt_pt.asc", "MapHLT2PT", MedDRARecord_hlt_pt.class),    LOW_LEVEL_TERMS         (MedDRA_MetadataType.ENTITYMAP, "llt.asc", "LowLevelTerms", MedDRARecord_llt.class),    SMQ_CONTENT             (MedDRA_MetadataType.MAP, "smq_content.asc", "SMQContent", MedDRARecord_smq_content.class);    private MedDRA_MetadataType dataType;    private String filename;    private String tablename;    private Class<?> classname;        MedDRA_Metadata(MedDRA_MetadataType dataType, String filename, String tablename, Class<?> classname){        this.dataType = dataType;        this.filename = filename;        this.tablename = tablename;        this.classname = classname;    }        public static List<String> getMapTables(){        ArrayList<String> tables = new ArrayList<String>();        MedDRA_Metadata [] allTables = MedDRA_Metadata.values();                for(int i=0; i < allTables.length; i++){            if(allTables[i].dataType == MedDRA_MetadataType.MAP ||                    allTables[i].dataType == MedDRA_MetadataType.ENTITYMAP){                tables.add(allTables[i].tablename);            }        }        return tables;    }    public static List<String> getEntityTables(){        ArrayList<String> tables = new ArrayList<String>();        MedDRA_Metadata [] allTables = MedDRA_Metadata.values();                for(int i=0; i < allTables.length; i++){            if(allTables[i].dataType == MedDRA_MetadataType.ENTITY ||                    allTables[i].dataType == MedDRA_MetadataType.ENTITYMAP){                tables.add(allTables[i].tablename);            }        }        return tables;    }        public static String getHistoryPropertyTable(){        return HISTORY.tablename;    }        public String filename(){        return this.filename;    }        public String tablename(){        return this.tablename;    }        @SuppressWarnings("unchecked")    public Class<DatabaseRecord> classname(){        return (Class<DatabaseRecord>) this.classname;    }}enum MedDRA_MetadataType{    INFO,    MAP,    ENTITY,    ENTITYMAP}